INSERT INTO `sanpham` (`id`, `tensp`, `mota`, `hinhanh`, `giagoc`, `giakm`, `id_loaisp`) VALUES
(1, 'ANLGIN C', 'Giảm đau, chống phong thấp, giải độc và hạ sốt. Đặc biệt dùng hạ sốt trong tất cả các trường hợp bệnh truyền nhiễm gây sốt như: tai xanh, dịch tả, tụ huyết trùng, phó thương hàn, đóng dấu, liên cầu, sốt sữa lợn và gia súc.\r\nThuốc dùng cho tất cả gia súc, ', 'image/img-product/dac tri/all/analgin-c.jpg', 40000, 0, 1),
(2, 'AURIS CLEAN', 'AURIS CLEAN có tác dụng khử mùi và nhẹ nhàng làm sạch tai, ống tai, giúp loại bỏ ráy tai, bụi bẩn, vảy sừng, mô bào chết. Phòng viêm tai, nhiễm trùng tai, chống viêm tái phát. Tạo môi trường thuận lợi giúp mô bào trong tai phát triển khỏe mạnh.', 'image/img-product/ho tro/cho meo/auris-clean.jpg', 7500, 0, 3),
(3, 'B.COMPLEX', 'Phòng và điều trị các bệnh do thiếu Vitamin nhóm B ở chó, mèo. Bổ sung các vitamin nhóm B cho cơ thể, giúp tăng cường sức đề kháng, phòng chống stress, phục hồi nhanh sau khi ốm, giúp tăng cường sự trao đổi chất, giúp hấp thu tối đa thức ăn, chống còi xươ', 'image/img-product/ho tro/cho meo/b.complex.jpg', 6000, 0, 3),
(4, 'AC IODINE', 'Thuốc sát khuẩn, chống nấm', 'image/img-product/ho tro/cho meo/ac iodine.jpg', 45000, 0, 3),
(5, 'AC Amitraz', 'Trị ghẻ, ve, chấy, rận, bọ chét trên chó, mèo.', 'image/img-product/dac tri/cho meo/ac amitraz.jpg', 38000, 0, 1),
(6, 'Anpar', 'Trị giun, sán.', 'image/img-product/ho tro/cho meo/6-1.jpg', 117000, 0, 3),
(7, 'Ac Dovet-Tab', 'Trị viêm mũi, viêm amidan, viêm phế quản-phổi gây ho, chảy nước mũi, nhiễm khuẩn dạ dày - ruột, \r\nnhiễm khuẩn da, viêm khớp, viêm vú, viêm tử cung.', 'image/img-product/khang sinh/cho meo/7-1.jpg', 185000, 0, 2),
(8, 'AC Ivemectin', 'Trị nội, ngoại ký sinh trùng', 'image/img-product/dac tri/cho meo/ac ivemectin.jpg', 12500, 0, 1),
(9, 'Ac Marbotab', ' Trị viêm da, nhiễm trùng đường tiết niệu, hô hấp trên chó, mèo.', 'image/img-product/dac tri/cho meo/ac marbotab.jpg', 245000, 0, 1),
(10, 'AC Pet Comfeel', 'Trị ve, ghẻ, chấy, rận, bọ chét trên chó, mèo.', 'image/img-product/ho tro/cho meo/ac pet comfell.jpg', 195000, 0, 3),
(11, 'Ac Genta Lyosin', 'Thuốc đặc trị các bệnh nhiễm trùng cấp: Viêm ruột, kiết lỵ, ỉa ra máu tươi, nôn mửa, bỏ ăn, bại liệt. Điều trị viêm phổi, viêm phế quản. Đặc biệt điều trị bệnh Ho cũi chó, suyễn, Lepto, Viêm đường niệu, sinh dục, tử cung gây xảy thai, Bệnh viêm đa khớp, t', 'image/img-product/khang sinh/cho meo/ac genta tylosin.jpg', 64000, 0, 2),
(12, 'Bio Amcoli-Plus', 'Phòng trị viêm ruột tiêu chảy, viêm xoang mũi, viêm móng, viêm khớp.', 'image/img-product/dac tri/cho meo/bio amcoli plus.jpg', 5500, 0, 1),
(13, 'Bio-Doxy Tabs', 'Chó, mèo: Chuyên trị nhiễm trùng đường hô hấp như viêm mũi, viêm hạch amidan và viêm phế quản do Pasteurella spp, Bordetella bronchiseptica, Staphylococcus aureus, Staphylococcus spp và Streptococcus spp; nhiễm ký sinh trùng Toxoplasma.', 'image/img-product/khang sinh/cho meo/bio doxy tabs.jpg', 11700, 0, 2),
(14, 'Bio Finil', 'Diệt ve, rận, bọ chét ở chó, mèo.', 'image/img-product/ho tro/cho meo/bio finil.jpg', 87500, 0, 3),
(15, 'BIO - GENTAZOL', 'Trị viêm tai ngoài trên chó.', 'image/img-product/dac tri/cho meo/bio gentazol.jpg', 25000, 0, 1),
(16, 'Bio Milk', 'BIO MILK For Pet với thành phần giàu chất dinh dưỡng, cân đối và rất dễ tiêu hóa tương tự sữa mẹ, có tác dụng thay thế sữa mẹ trong trường hợp chó, mèo mất sữa, kém sữa hoặc thiếu sữa do bầy con quá đông, đồng thời là nguồn bổ sung chất dinh dưỡng cho chó', 'image/img-product/ho tro/cho meo/bio milk.jpg', 45000, 0, 3),
(17, 'Bio Rantel', 'Tẩy sạch các loại giun sán ký sinh trong ruột chó, mèo như: Giun đũa, giun móc, giun tóc, sán dây.', 'image/img-product/dac tri/cho meo/bio rantel plus.jpg', 60000, 0, 1),
(18, 'Bio Scour W.S.P', 'Đặc trị bệnh viêm ruột, tiêu chảy, ó mửa ở chó, mèo.', 'image/img-product/khang sinh/cho meo/Bio Scour W.S.P.jpg', 29000, 0, 2),
(19, 'Bio Shampo 4 Jolie', 'Dùng để tắmchó và mèo. Làm sạch lông, chống rụng lông. Khử mùi hôi. Dưỡng lông, giúp lông siêu mượt. Phòng ngừa nhiễm ghẻ và nấm da.', 'image/img-product/ho tro/cho meo/Bio Shampo 4 Jolie.jpg', 97400, 0, 3),
(20, 'Bio Shampoo5 (derma)', 'Phòng và trị ghẻ trên chó do demodex, sarcoptes, bọ chét kết hợp với nấm da gây triệu chứng: Hôi lông, rụng lông, khô da hoặc chảy dịch vàng, chảy máu trên da.', 'image/img-product/ho tro/cho meo/Bio Shampoo5 (derma).jpg', 75000, 0, 3),
(21, 'Bio Vit Plus', 'Cung cấp vitamin và chất điện giải giúp thú cưng mau lành bệnh, ăn nhiều, bộ lông bóng mượt, chống rụng lông cho chó, mèo.', 'image/img-product/ho tro/cho meo/Bio Vit plus.jpg', 4800, 0, 3),
(22, 'Biotic', 'Cung cấp vitamin và vi khuẩn có lợi, tăng cường tiêu hóa, phòng ngừa viêm ruột, tiêu chảy, phù đầu trên gia súc, gia cầm, thú cưng.', 'image/img-product/ho tro/cho meo/Biotic.jpg', 7000, 0, 3),
(23, 'Drich Doxy Sul Trep', 'Đặc trị các bệnh chó mèo: Kiết lị, ỉa ra máu tươi, sốt, nôn mửa, bỏ ăn. Viêm mũi, xoang, phế quản, phổi. Viêm tử cung, vú, khớp, móng. Nhiễm trùng sau sinh sản.', 'image/img-product/khang sinh/cho meo/Drich Doxy Sul Trep.jpg', 123750, 0, 2),
(24, 'ND Ketomycin', 'Đặc trị các bệnh nấm toàn thân, đường tiêu hóa, sinh dục và tiết niệu. Biểu hiện như: Rụng lông, nổi mụn từ trong da, thối lỗ chân lông, bong vẩy, nứt da thịt…. Bảo vệ vết thương, chống nhiễm khuẩn, nhiễm nấm, giúp vết thương mau lành.', 'image/img-product/dac tri/cho meo/ND Ketomycin.jpg', 37000, 0, 1),
(25, 'VB Livo Plus For Pet', ' Bảo vệ gan, thận trong các bệnh gây tổn thương trên gan, thận như: Ký sinh trùng đường máu. Đào thải những chất độc ở gan, thận và trong cơ thể khi mắc bệnh hoặc sau khi điều trị bằng kháng sinh, gan nhiễm mỡ. Tăng miễn dịch, tăng sức đề kháng, tăng tiết', 'image/img-product/ho tro/cho meo/VB Livo Plus For Pet.jpg', 66000, 0, 3),
(26, 'VMD Diptivet', 'Trị nhiễm trùng đường hô hấp trên heo do vi khuẩn nhạy với Tildipirosin gây ra (như viêm phổi-màng phổi, tụ huyết trùng, viêm teo xoang mũi truyền nhiễm, bệnh Glasser-viêm đa xoang', 'image/img-product/ho tro/cho meo/VMD Diptivet.jpg', 21200, 0, 3),
(27, 'VMD Micona shampoo', 'Dùng để tắm cho chó và mèo.\r\n- Micona Shampoo chứa các hoạt chất đặc biệt và mùi hương dịu nhẹ giúp làm sạch lông, chống rụng lông, giữ ẩm da, khử mùi hôi, dưỡng lông, giúp lông siêu mượt.\r\n- Phòng trị các bệnh da lông rất thường xảy ra trên thú cưng như:', 'image/img-product/ho tro/cho meo/VMD Micona shampoo.jpg', 145000, 0, 3),
(28, 'Viavet AZ Doxy 50%S', 'Gia cầm: Đặc trị viêm mũi, viêm phổi (CRD), viêm phổi ghép, viêm đường tiêu hóa với các biểu hiện: hen nặng, hen sưng phù đầu, hen ghép E.coli kết hợp (CCRD), hen ghép tiêu chảy, Coryza, bạch lỵ, thương hàn. \r\nHeo: Đặc trị bệnh viêm phổi do Mycoplasma gây', 'image/img-product/khang sinh/gia cam gia suc/viavet az doxy.jpg', 2750000, 0, 2),
(29, 'Via Floazmax 50 (FLO 50%)', 'Gia cầm: Đặc trị (CRD) - khò khe, CRD sưng đầu, CRD ghép E.coli kết hợp (CCRD), CRD ghép ORT, CRD ghép tiêu chảy, Coryza, bạch lỵ, thương hàn.\r\nHeo: Đặc trị bệnh viêm phổi do Mycoplasma gây ho khan, ho kéo dài; viêm phổi dính sườn, phó thương hàn, tụ huyế', 'image/img-product/khang sinh/gia cam gia suc/via floazmax.jpg', 2200000, 0, 2),
(30, 'VIAVET Flotec 25', 'AZ.FLOTEC 25 được đặc chế cho gia cầm. Đặc trị: ORT, CRD (bệnh hen gà), hen sưng phù đầu, hen ghép E.coli kết hợp (CCRD), CRD ghép tiêu chảy, CRD ghép Coryza, bạch lỵ, thương hàn. Hiệu quả cao khi các thuốc khác đã bị nhờn.', 'image/img-product/khang sinh/gia cam/VIAVET Flotec 25.jpg', 1650000, 0, 2),
(31, 'VB MOZILA ( AMOX 50% COLISTIN)', 'Trị tụ huyết trùng, hô hấp, viêm ruột hoại tử thương hàn, tiêu chảy, phân xanh, phân vàng, viêm da, viêm vú viêm tử cung trên trâu, bò, lợn, gia cầm.', 'image/img-product/khang sinh/gia cam gia suc/vb mozila.jpg', 1250000, 0, 2),
(32, 'VIAVET A FLODOX C Premix', '- Gà, vịt, ngan, cút: Đặc trị các bệnh đường hô hấp như: hen khò khè khó thở (CRD), bệnh hen ghép E.coli (CCRD), tụ huyết trùng (toi), thương hàn, sưng mặt, vẩy mỏ ( bệnh hen khẹc ở ngan, vịt), viêm ruột - tiêu chảy.\r\n- Heo, trâu, bò, dê, cừu: Đặc trị các', 'image/img-product/khang sinh/gia cam gia suc/VIAVET A FLODOX C Premix.jpg', 760000, 0, 2),
(33, 'Viavet GENTA TYLOSIN', 'Trâu, bò, heo, dê, bê, chó: viêm phổi, ho suyễn, chảy nước mũi, kiết lỵ, ỉa ra máu. Bệnh phân trắng, phân vàng, bệnh sưng phù đầu của heo con.\r\nGà, vịt, ngan, chim cút: hen gà (CRD) thở khò khè, hen ghép đi ỉa, hen sưng đầu, khẹc vịt, bại liệt, sưng đầu, ', 'image/img-product/khang sinh/gia cam gia suc/Viavet GENTA TYLOSIN.jpg', 520000, 0, 2),
(34, 'ND Coli Amoxy', 'Trâu bò, dê cừu: Điều trị các bệnh đường tiêu hóa, hô hấp, đặc biệt các bệnh do E.coli, tụ huyết trùng. Gà, vịt: Tiêu chảy nặng, phân có dịch nhầy màu nâu, xanh hoặc trắng, viêm ruột hoại tử, viêm khớp, viêm rốn, viêm phổi…\r\nLợn: Tiêu chảy nhiều gây mất n', 'image/img-product/khang sinh/gia cam gia suc/ND Coli Amoxy.jpg', 510000, 0, 2),
(35, 'Bio Amox Tyloxin', 'Phòng ngừa và điều trị rất công hiệu bệnh đường HÔ HẤP (CRD ở gia cầm, viêm phổi do Mycoplasma ở heo), nhiễm trùng đường TIÊU HÓA, tiêu chảy, nhiễm trùng cơ quan TIẾT NIỆU và SINH DỤC.', 'image/img-product/khang sinh/gia cam gia suc/Bio Amox tyloxin.jpg', 349800, 0, 2),
(36, 'Bio AMPI COLI MAX', 'Đặc trị các bệnh tiêu chảy phân xanh, phân trắng, phân vàng nhớt có máu, thương hàn, sưng phù đầu, tụ huyết trùng, viêm phổi, viêm màng phổi gây ra bởi vi khuẩn E.coli, Salmonella, Clostridium, Mycoplasma, Heamophilus spp.', 'image/img-product/dac tri/gia cam gia suc/Bio AMPI COLI MAX.jpg', 320000, 0, 1),
(37, 'NThai TIC', 'Lợn: Điều trị phân trắng, phân vàng, phân bùn, hồng lỵ (kiết lỵ), ỉa chảy mất nước, bệnh phù thũng lợn con, viêm ruột hoại tử, tụ huyết trùng, thương hàn…\r\n  Gà, vịt, ngan, ngỗng, chim cút: phân xanh, phân trắng, phân nhớt vàng, mào tham, phó thương hàn, ', 'image/img-product/khang sinh/gia cam gia suc/NThai TIC 10g.jpg', 320000, 0, 2),
(38, 'ND TD Ceftisol (ceftifour) LA', 'Đặc trị viêm vú, viêm vú tiềm ẩn, viêm vú soma, viêm vú mất sữa (MMA), viêm tử cung, viêm móng, viêm ruột tiêu chảy, viêm phổi, viêm màng phổi, phổi dính sườn, phổi hóa mủ, ho, hen suyễn, sốt bỏ ăn không rõ nguyên nhân, các bệnh kế phát của bệnh tai xanh ', 'image/img-product/khang sinh/gia suc/ND TD Ceftisol (ceftifour) LA.jpg', 299000, 0, 2),
(39, 'ND Bactan LA', 'Đặc trị viêm vú, viêm tử cung, cạn sữa, tiêu chảy, nhiễm trùng đường hô hấp, hen suyễn, viêm phổi, viêm màng phổi, kế phát bệnh truyền nhiễm tai xanh (sốt đỏ, PRRS), LMLM, viêm da, viêm kẽ móng, viêm đường tiết niệu, tụ huyết trùng, phó thương hàn, lepto,', 'image/img-product/dac tri/cho meo gia suc/ND Bactan LA.jpg', 298000, 0, 1),
(40, 'VIAVET Ceftiketo', '- Đặc trị viêm phổi truyền nhiễm, viêm phổi dính sườn (APP), glasser, viêm\r\nphổi, viêm phế quản, viêm hoại tử, liên cầu khuẩn, nhiễm trùng mô mềm,\r\nviêm cơ, áp xe, tụ huyết trùng trên trâu, bò, ngựa, dê, cừu, heo.\r\n- Trị chứng viêm vú, sốt sữa, viêm tử cu', 'image/img-product/dac tri/gia suc/VIAVET Ceftiketo.jpg', 285000, 0, 1),
(41, 'Viavet Amox LA 20%', 'Đặc trị tất cả các bệnh truyền nhiễm gây ra bởi vi khuẩn nhạy cảm với Amoxicillin như: Bệnh viêm phổi dính sườn(APP), viêm đường tiết niệu - sinh dục, viêm vú - viêm tử cung - mất sữa (MMA), viêm da tiết dịch, viêm ruột hoại tử, hồng lỵ, tụ huyết trùng, đ', 'image/img-product/dac tri/gia suc/viavet amox la 20.jpg', 165000, 0, 1),
(42, 'Bio Tylo Dox Plus', '-Phòng và trị các bệnh nhiễm trùng đường hô hấp như bệnh viêm phổi, tụ huyết trùng, khò khè, CRD, bệnh suyển heo gây ra bởi Mycoplasma.\r\n-Phòng và trị các bệnh nhiễm trùng đường tiêu hóa gây tiêu chảy, bệnh hồng lỵ, phó thương hàn, E.coli ở heo, bê, nghé,', 'image/img-product/khang sinh/gia cam gia suc/Bio Tylo Dox Plus.jpg', 143300, 0, 2),
(43, 'Việt Anh Amoxyl LA 15%', 'Đặc trị các bệnh nhiễm khuẩn tiêu hóa (E.coli, Clostridium spp., sưng phù đầu, viêm dạ dày-ruột…), hô hấp (viêm phế quản-phổi, suyễn, APP…), niệu-sinh dục (M.M.A, sốt sữa viêm bàng quang, niệu đạo…), Lepto, đóng dấu, tụ huyết trùng, khớp, mô mềm và da, ho', 'image/img-product/dac tri/cho meo gia suc/vietanh amoxyl.jpg', 135000, 0, 1),
(44, 'Bio Marcosone', 'Trị bệnh truyền nhiễm do vi khuẩn Gr(-), Gr(+) v Mycoplasma.', 'image/img-product/dac tri/all/Bio Marcosone.jpg', 120000, 0, 1),
(45, 'Bio Apracin 200', 'Trị viêm đường tiêu hoá, hô hấp, viêm vú, viêm khớp trên bê, nghé, heo, dê, cừu, chó, mèo.', 'image/img-product/dac tri/cho meo gia suc/Bio Apracin 200.jpg', 119000, 0, 1),
(46, 'Bio Linco', 'Điều trị nhiễm trùng hô hấp, tiêu hóa, sinh dục, ngoài da, áp xe', 'image/img-product/khang sinh/all/Bio Linco 100ml.jpg', 78000, 0, 1),
(47, 'Bio Codexin', 'Đặc trị các bệnh nhiễm trùng trên gia súc, gia cầm:\r\n Heo: Phù đầu, viêm ruột tiêu chảy, viêm phổi, tụ huyết trùng, viêm vú, viêm tử cung, hội chứng MMA. \r\nTrâu, bò, dê, cừu: Viêm vú, viêm tử cung, viêm ruột tiêu chảy, viêm phổi, đau móng. \r\nChó, mèo: Viê', 'image/img-product/khang sinh/all/Bio Codexin.jpg', 48800, 0, 2),
(48, 'Bio CRD (Spiramycin + Oxytetra + Brom)', 'Trị nhiễm trùng đường hô hấp như: CRD, CRD kết hợp E.coli, viêm xoang mũi, viêm phổi trên gà, vịt, cút.', 'image/img-product/khang sinh/gia cam/Bio CRD (Spiramycin + Oxytetra + Brom).jpg', 38000, 0, 2),
(49, 'Viavet Apracoli', 'Đặc hiệu với bệnh sưng phù đầu do E.coli dung huyết ở heo con với các biểu hiện: Sưng phù đầu, hầu, họng, mí mắt, kêu khản tiếng, co giật, đâm đầu vào tường, thường chết con to, chết nhanh. Đặc biệt hiệu quả trong điều trị các biểu hiện kế phát như: Phân ', 'image/img-product/khang sinh/cho meo gia suc/Viavet Apracoli.jpg', 21000, 0, 2),
(50, 'Viavet Achymosin', 'Sự kết hợp đặc biệt giữa Alfachymotrypsin và Trypsin giúp:\r\n- Tiêu viêm bằng cách phá hủy các phản ứng miễn dịch gây ra sự tích lũy fibrine trong các xoang, đường hô hấp.\r\n- Tăng vi tuần hoàn trong mô bị viêm nhờ hoạt động tiêu sợi huyết, đánh tan máu bầm', 'image/img-product/dac tri/all/Viavet Achymosin.jpg', 460000, 0, 1),
(51, 'VIME Tobra', ' Đặc trị viêm vú, viêm tử cung, viêm tiết niệu.', 'image/img-product/dac tri/all/VIME Tobra.jpg', 350000, 0, 1),
(52, 'ND Eco Para C', '– Đặc trị cảm cúm, hạ sốt tiêu viêm, an thần, giảm đau, tăng lực,  ngăn ngừa stress, hỗ trợ điều trị bệnh truyền nhiễm. Các bệnh sốt không rõ nguyên nhân ở lợn, gà, vịt, ngan, cút như: Sốt đỏ kế phát bệnh lepto, tụ huyết trùng, phó thương hàn ở lợn. Bệnh ', 'image/img-product/dac tri/all/ND Eco Para C.jpg', 205000, 0, 1),
(53, 'Bio Bromhexine', 'Giảm ho, long đờm, chống co thắt phế quản khi viêm phế quản, viêm thanh phế quản truyền nhiễm, viêm phổi, suyễn, CRD ở gia súc, gia cầm.', 'image/img-product/dac tri/gia cam gia suc/Bio Bromhexine.jpg', 135900, 0, 1),
(54, 'Bio Ketosol 100', 'Thuốc có tác dụng giảm đau hạ sốt nhanh, giảm phù nề và kháng viêm thuộc nhóm không steroid\r\n- Heo: kết hợp với kháng sinh để trị đường hô hấp; viêm vú, viêm tử cung, mất sữa (hội chứng MMA).\r\n- Trâu, bò, dê, cừu: Hỗ trợ điều trị bệnh viêm vú, viêm phổi c', 'image/img-product/dac tri/cho meo gia suc/Bio Ketosol 100.jpg', 105000, 0, 1),
(55, 'Bio Diclofenac', 'Kháng viêm, giảm đau, hạ sốt trên trâu, bò, ngựa, dê, cừu, heo, chó, mèo.', 'image/img-product/dac tri/cho meo gia suc/Bio Diclofenac.jpg', 59000, 0, 1),
(56, 'Bio Cep Mas', 'Điều trị bệnh viêm vú do Staphylococcus aureus, Streptococcus dysgalactiae, E.coli trên bò, dê, cừu đang cho sữa.', 'image/img-product/dac tri/gia suc/Bio Cep Mas.jpg', 48000, 0, 1),
(57, 'Bio Dexa', 'Heo, trâu, bò, ngựa, dê, cừu, chó, mèo: Chống dị ứng, kích thích cơ thể gia tăng phân giải gluco nhằm chống stress, chống viêm trong các bệnh nhiễm trùng hoặc các trường hợp viêm không do nhiễm trùng.', 'image/img-product/dac tri/cho meo gia suc/Bio Dexa.jpg', 44400, 0, 1),
(58, 'Bio Anazin C', 'Heo, trâu, bò, dê, cừu, chó, mèo:Hạ sốt trong các bệnh nhiễm trùng cấp, kháng viêm, chống stress, giảm đau.', 'image/img-product/dac tri/cho meo gia suc/Bio Anazin C.jpg', 330000, 0, 1),
(59, 'ND Eco Brom Ho', 'Long đờm, thải đờm giảm ho, cắt cơn hen suyễn. Hỗ trợ và điều trị bệnh viêm phế quản, phổi, viêm phổi phế, tụ huyết trùng, viêm xoang, viêm teo mũi truyền nhiễm và các bệnh truyền nhiễm khác gây ho do nhiễm vi khuẩn, vi rút. Bệnh CRD, CCRD, ORT, sưng phù ', 'image/img-product/dac tri/all/ND Eco Brom Ho.jpg', 36000, 0, 1),
(60, 'NThai Anti Gum (Para 20% + Neomycin)', '- Thuốc đặc trị Gumboro và Gumboro ghép E.coli, hỗ trợ điều trị các bệnh nhiễm trùng gây sốt như: \r\n- Bệnh viêm gan ruột thừa truyền nhiễm, \r\n- Ký sinh trùng máu (Leucocytozoonosis), Niu cát-xơn, cúm gà, tụ huyết trùng,... \r\n- Chống bội nhiễm do các vi kh', 'image/img-product/dac tri/gia cam/NThai Anti Gum.jpg', 35200, 0, 1),
(61, 'Bio Bromhexine', 'Giảm ho, long đờm, chống co thắt phế quản trong các bệnh viêm phế quản, viêm phổi ở gia súc.', 'image/img-product/dac tri/cho meo gia suc/Bio Bromhexine.jpg', 26200, 0, 1),
(62, 'BETA GLUCAN C', '- Cung cấp các vitamin, khoáng, acid amin và đặc biệt là Beta-Glucan cho vật nuôi, giúp vật nuôi có sức đề kháng tốt để chống đỡ bệnh. Beta-Glucan là chất có tác dụng kích thích hệ thống miễn dịch tự nhiên của cơ thể sinh kháng thể để phòng ngừa các bệnh:', 'image/img-product/ho tro/gia cam gia suc/beta glucan.jpg', 179000, 0, 3),
(63, 'Bio Metasal', 'Điều chỉnh rối loạn trao đổi chất do mắc bệnh hoặc do thiếu dinh dưỡng, giúp thú mau hồi phục khi mắc bệnh, mau lớn.\r\nPhục hồi các rối loạn sinh sản như khô thai, sẩy thai, chậm động dục sau khi sanh trên trâu, bò, dê, cừu; chậm lên giống sau khi cai sữa ', 'image/img-product/ho tro/all/Bio Metasal.jpg', 165800, 0, 3),
(64, 'ND Butosal', ' Butosal kích thích quá trình chuyển hóa các chất và điều chỉnh tất cả các rối loạn trao đổi chất gây ra do thức ăn, chăm sóc kém, dịch bệnh. \r\nRối loạn dinh dưỡng và tăng trưởng kém ở thú non do bệnh tật. \r\nĐiều trị bệnh ở thời kỳ sinh sản và hỗ trợ điều', 'image/img-product/ho tro/all/ND Butosal.jpg', 136000, 0, 3),
(65, 'Viavet Atropin Max', '- Giảm đau, giảm nhu động co thắt ruột, bệnh lồng ruột, viêm ruột cấp. Ngăn ngừa ngừng tim và hô hấp trong gâp mê; giảm sự tiết dịch phế quản trong gây mê.\r\n- Kết hợp với kháng sinh điều trị phù phổi gia súc, đặc biệt viêm phổi cấp tính ở ngựa.\r\n- Kích th', 'image/img-product/ho tro/all/Viavet Atropin Max.jpg', 64000, 0, 3),
(66, 'Bio Sorbitol B12', 'Tăng cường chức năng gan, tăng khả năng tiêu hóa, phòng ngừa ngộ độc do độc tố của nấm mốc, bệnh gan nhiễm mỡ, giúp hồi phục cơ thể sau các bệnh nhiễm trùng trên heo, heo con, gia cầm.', 'image/img-product/ho tro/gia cam gia suc/Bio Sorbitol B12.jpg', 94000, 0, 3);

